import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Plus, Edit, Trash2 } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface Product {
  id: number;
  name: string;
  hsnCode: string;
  category: string | null;
  rate: string;
  gstPercentage: string;
  quantity: number;
  comments: string | null;
}

export default function InventoryManagement() {
  const { toast } = useToast();
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    hsnCode: "",
    category: "",
    rate: "",
    gstPercentage: "18",
    quantity: "0",
    comments: "",
  });

  const { data: products = [], isLoading: loading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const addMutation = useMutation({
    mutationFn: async (data: Omit<typeof formData, 'quantity'> & { quantity: number }) => {
      return await apiRequest("POST", "/api/products", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({
        title: "Success",
        description: "Product added successfully",
      });
      setIsAddDialogOpen(false);
      setFormData({ name: "", hsnCode: "", category: "", rate: "", gstPercentage: "18", quantity: "0", comments: "" });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to add product",
      });
    },
  });

  const handleAdd = async () => {
    if (!formData.name || !formData.hsnCode || !formData.rate) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please fill in all required fields",
      });
      return;
    }

    const dataToSend = {
      ...formData,
      quantity: parseInt(formData.quantity) || 0,
    };

    addMutation.mutate(dataToSend);
  };

  const editMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: Omit<typeof formData, 'quantity'> & { quantity: number } }) => {
      return await apiRequest("PATCH", `/api/products/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({
        title: "Success",
        description: "Product updated successfully",
      });
      setIsEditDialogOpen(false);
      setEditingProduct(null);
      setFormData({ name: "", hsnCode: "", category: "", rate: "", gstPercentage: "18", quantity: "0", comments: "" });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to update product",
      });
    },
  });

  const handleEdit = async () => {
    if (!editingProduct || !formData.name || !formData.hsnCode || !formData.rate) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please fill in all required fields",
      });
      return;
    }

    const dataToSend = {
      ...formData,
      quantity: parseInt(formData.quantity) || 0,
    };

    editMutation.mutate({ id: editingProduct.id, data: dataToSend });
  };

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      return await apiRequest("DELETE", `/api/products/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({
        title: "Success",
        description: "Product deleted successfully",
      });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to delete product",
      });
    },
  });

  const handleDelete = async (id: number) => {
    if (!confirm("Are you sure you want to delete this product?")) return;
    deleteMutation.mutate(id);
  };

  const openEditDialog = (product: Product) => {
    setEditingProduct(product);
    setFormData({
      name: product.name,
      hsnCode: product.hsnCode,
      category: product.category || "",
      rate: product.rate,
      gstPercentage: product.gstPercentage,
      quantity: String(product.quantity || 0),
      comments: product.comments || "",
    });
    setIsEditDialogOpen(true);
  };

  return (
    <div className="p-8 max-w-7xl mx-auto">
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-semibold mb-2">Inventory Management</h1>
          <p className="text-muted-foreground">Manage products and pricing</p>
        </div>
        <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-add-product">
              <Plus className="h-4 w-4 mr-2" />
              Add Product
            </Button>
          </DialogTrigger>
          <DialogContent className="max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Add New Product</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="name">Product Name <span className="text-destructive">*</span></Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  placeholder="Enter product name"
                  data-testid="input-product-name"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="hsnCode">HSN Code <span className="text-destructive">*</span></Label>
                <Input
                  id="hsnCode"
                  value={formData.hsnCode}
                  onChange={(e) => setFormData({ ...formData, hsnCode: e.target.value })}
                  placeholder="Enter HSN code (e.g. 8471)"
                  data-testid="input-hsn-code"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="category">Category</Label>
                <Input
                  id="category"
                  value={formData.category}
                  onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                  placeholder="Enter category (optional)"
                  data-testid="input-product-category"
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="rate">Rate () <span className="text-destructive">*</span></Label>
                  <Input
                    id="rate"
                    type="number"
                    step="0.01"
                    value={formData.rate}
                    onChange={(e) => setFormData({ ...formData, rate: e.target.value })}
                    placeholder="0.00"
                    data-testid="input-product-rate"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="gst">GST % <span className="text-destructive">*</span></Label>
                  <Input
                    id="gst"
                    type="number"
                    step="0.01"
                    min="0"
                    max="100"
                    value={formData.gstPercentage}
                    onChange={(e) => setFormData({ ...formData, gstPercentage: e.target.value })}
                    placeholder="Enter GST %"
                    data-testid="input-gst-percentage"
                  />
                </div>
              </div>
              <div className="space-y-2">
                <Label htmlFor="quantity">Stock Quantity</Label>
                <Input
                  id="quantity"
                  type="number"
                  min="0"
                  value={formData.quantity}
                  onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
                  placeholder="0"
                  data-testid="input-product-quantity"
                />
              </div>
              <div className="space-y-2">
