import { SHOP_INFO } from "@shared/shopInfo";
import { useState } from "react";
import logoImg from "@assets/1762677792449-0d76ba93-0eba-48fa-927f-62011c24e28f_1_1762963626825.jpg";

interface InvoiceReceiptProps {
  invoiceNumber: string;
  customerName: string;
  customerPhone?: string;
  date?: Date;
  items: Array<{
    itemName: string;
    hsnCode: string;
    quantity: number;
    rate: string;
    taxableValue: number;
    cgstAmount: number;
    sgstAmount: number;
    total: number;
  }>;
  subtotal: number;
  grandTotal: number;
  paid?: number;
}

export function InvoiceReceipt({
  invoiceNumber,
  customerName,
  customerPhone,
  date = new Date(),
  items,
  subtotal,
  grandTotal,
  paid = 0,
}: InvoiceReceiptProps) {
  const balance = grandTotal - paid;
  const [logoError, setLogoError] = useState(false);

  const formatDate = (date: Date) => {
    return date.toLocaleDateString("en-IN", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
    });
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString("en-IN", {
      hour: "2-digit",
      minute: "2-digit",
      hour12: true,
    });
  };

  return (
    <div className="hidden print:block" data-testid="invoice-receipt">
      <style>
        {`
          @media print {
            @page {
              size: 80mm auto;
              margin: 0;
            }
            body {
              margin: 0;
              padding: 0;
            }
            .no-print {
              display: none !important;
            }
          }
        `}
      </style>

      <div className="receipt-container" style={{
        width: "80mm",
        maxWidth: "300px",
        margin: "0 auto",
        padding: "10px",
        fontFamily: "monospace",
        fontSize: "12px",
        lineHeight: "1.4",
        color: "#000",
        backgroundColor: "#fff",
      }}>
        {/* Header with Logo */}
        <div style={{ textAlign: "center", marginBottom: "10px" }}>
          {!logoError && (
            <img 
              src={logoImg} 
              alt={SHOP_INFO.name}
              onError={() => setLogoError(true)}
              style={{
                maxWidth: "60px",
                maxHeight: "60px",
                margin: "0 auto 8px",
                display: "block",
              }}
            />
          )}
          <div style={{ fontSize: "16px", fontWeight: "bold", marginBottom: "4px" }}>
            {SHOP_INFO.name}
          </div>
          <div style={{ fontSize: "10px", marginBottom: "2px", whiteSpace: "pre-line" }}>
            {SHOP_INFO.address}
          </div>
          <div style={{ fontSize: "10px", marginBottom: "2px" }}>
            Phone: {SHOP_INFO.phone}
          </div>
          <div style={{ fontSize: "10px", marginBottom: "8px" }}>
            GST: {SHOP_INFO.gstNumber}
          </div>
        </div>

        {/* Divider */}
        <div style={{
          borderTop: "1px dashed #000",
          margin: "8px 0",
        }} />

        {/* Invoice Details */}
        <div style={{ marginBottom: "8px", fontSize: "11px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "4px" }}>
            <span style={{ fontWeight: "bold" }}>Invoice: {invoiceNumber}</span>
          </div>
          <div style={{ marginBottom: "2px" }}>
            Customer: {customerName}
          </div>
          <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "2px" }}>
            <span>Date: {formatDate(date)}</span>
            <span>Time: {formatTime(date)}</span>
          </div>
        </div>

        {/* Divider */}
        <div style={{
          borderTop: "1px dashed #000",
          margin: "8px 0",
        }} />

        {/* Items Table */}
        <div style={{ marginBottom: "8px" }}>
          {items.map((item, index) => (
            <div key={index} style={{ marginBottom: "6px", fontSize: "10px" }}>
              <div style={{ display: "flex", justifyContent: "space-between", fontWeight: "bold", marginBottom: "2px" }}>
                <div style={{ flex: "1" }}>{item.itemName}</div>
                <div style={{ textAlign: "right" }}>{item.total.toFixed(2)}</div>
              </div>
              <div style={{ fontSize: "9px", color: "#333", marginBottom: "2px" }}>
                HSN: {item.hsnCode} | Qty: {item.quantity} x {parseFloat(item.rate).toFixed(2)}
              </div>
              <div style={{ fontSize: "9px", color: "#555", display: "flex", justifyContent: "space-between" }}>
                <span>Taxable: {item.taxableValue.toFixed(2)}</span>
                <span>CGST: {item.cgstAmount.toFixed(2)} | SGST: {item.sgstAmount.toFixed(2)}</span>
              </div>
              {index < items.length - 1 && (
                <div style={{ borderTop: "1px dotted #ccc", marginTop: "4px" }} />
              )}
            </div>
          ))}
        </div>

        {/* Divider */}
        <div style={{
          borderTop: "1px dashed #000",
          margin: "8px 0",
        }} />

        {/* Totals */}
        <div style={{ fontSize: "11px", marginBottom: "8px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "2px" }}>
            <span>Taxable Value:</span>
            <span>{subtotal.toFixed(2)}</span>
          </div>
          <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "2px", fontSize: "10px" }}>
            <span>CGST:</span>
            <span>{items.reduce((sum, item) => sum + item.cgstAmount, 0).toFixed(2)}</span>
          </div>
          <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "4px", fontSize: "10px" }}>
            <span>SGST:</span>
            <span>{items.reduce((sum, item) => sum + item.sgstAmount, 0).toFixed(2)}</span>
          </div>
          <div style={{
            display: "flex",
            justifyContent: "space-between",
            marginBottom: "4px",
            fontWeight: "bold",
            fontSize: "13px",
          }}>
            <span>Grand Total:</span>
            <span>{grandTotal.toFixed(2)}</span>
          </div>
          {paid > 0 && (
            <>
              <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "4px" }}>
                <span>Paid:</span>
                <span>{paid.toFixed(2)}</span>
              </div>
              <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "4px" }}>
                <span>Balance:</span>
                <span>{balance.toFixed(2)}</span>
              </div>
            </>
          )}
        </div>

        {/* Divider */}
        <div style={{
          borderTop: "1px dashed #000",
          margin: "8px 0",
        }} />

        {/* Footer Message */}
        <div style={{
          textAlign: "center",
          fontSize: "11px",
          fontStyle: "italic",
          marginTop: "8px",
        }}>
          Thank you for your business!
        </div>
      </div>
    </div>
  );
}

client/src/pages/reports.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Download } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

export default function Reports() {
  const { toast } = useToast();
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [expensesStartDate, setExpensesStartDate] = useState("");
  const [expensesEndDate, setExpensesEndDate] = useState("");

  const downloadMutation = useMutation({
    mutationFn: async ({ startDate, endDate }: { startDate: string; endDate: string }) => {
      return await apiRequest("GET", `/api/reports/sales?startDate=${startDate}&endDate=${endDate}`);
    },
    onSuccess: async (response, variables) => {
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `sales-report-${variables.startDate}-to-${variables.endDate}.xlsx`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "Success",
        description: "Sales report downloaded successfully",
      });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to generate sales report",
      });
    },
  });

  const downloadExpensesMutation = useMutation({
    mutationFn: async ({ startDate, endDate }: { startDate: string; endDate: string }) => {
      return await apiRequest("GET", `/api/reports/expenses?startDate=${startDate}&endDate=${endDate}`);
    },
    onSuccess: async (response, variables) => {
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `expenses-report-${variables.startDate}-to-${variables.endDate}.xlsx`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "Success",
        description: "Expenses report downloaded successfully",
      });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to generate expenses report",
      });
    },
  });

  const handleDownload = async () => {
    if (!startDate || !endDate) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please select both start and end dates",
      });
      return;
    }

    downloadMutation.mutate({ startDate, endDate });
  };

  const handleExpensesDownload = async () => {
    if (!expensesStartDate || !expensesEndDate) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please select both start and end dates",
      });
      return;
    }

    downloadExpensesMutation.mutate({ startDate: expensesStartDate, endDate: expensesEndDate });
  };

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-semibold mb-2">Reports</h1>
        <p className="text-muted-foreground">Download and analyze business reports</p>
      </div>

      <Tabs defaultValue="sales" className="w-full">
        <TabsList className="mb-6">
          <TabsTrigger value="sales" data-testid="tab-sales-report">Sales Report</TabsTrigger>
          <TabsTrigger value="expenses" data-testid="tab-expenses-report">Expenses Report</TabsTrigger>
        </TabsList>
        <TabsContent value="sales">

      <Card>
        <CardHeader>
          <CardTitle className="text-lg font-medium">Generate Report</CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="grid md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <Label htmlFor="startDate" className="text-sm font-medium">
                Start Date <span className="text-destructive">*</span>
              </Label>
              <Input
                id="startDate"
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                className="h-12"
                data-testid="input-report-start-date"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="endDate" className="text-sm font-medium">
                End Date <span className="text-destructive">*</span>
              </Label>
              <Input
                id="endDate"
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                className="h-12"
                data-testid="input-report-end-date"
              />
            </div>
          </div>

          <div className="bg-muted p-6 rounded-md space-y-2">
            <h3 className="font-semibold text-sm mb-3">Report Includes:</h3>
            <ul className="text-sm text-muted-foreground space-y-1">
              <li> Total Sales (Revenue)</li>
              <li> Category-wise Sales Breakdown (%)</li>
              <li> Product-wise Sales Analysis</li>
              <li> Total B2B vs B2C Sales Comparison</li>
              <li> Payment Mode Distribution (Cash vs Online)</li>
              <li> Invoice Count and Average Order Value</li>
            </ul>
          </div>

          <Button
            className="w-full h-12"
            onClick={handleDownload}
            disabled={downloadMutation.isPending}
            data-testid="button-download-report"
          >
            <Download className="h-4 w-4 mr-2" />
            {downloadMutation.isPending ? "Generating Report..." : "Download Excel Report"}
          </Button>
        </CardContent>
      </Card>
        </TabsContent>

        <TabsContent value="expenses">
          <Card>
            <CardHeader>
              <CardTitle className="text-lg font-medium">Generate Expenses Report</CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="grid md:grid-cols-2 gap-6">
                <div className="space-y-2">
                  <Label htmlFor="expensesStartDate" className="text-sm font-medium">
                    Start Date <span className="text-destructive">*</span>
                  </Label>
                  <Input
                    id="expensesStartDate"
                    type="date"
                    value={expensesStartDate}
                    onChange={(e) => setExpensesStartDate(e.target.value)}
                    className="h-12"
                    data-testid="input-expenses-start-date"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="expensesEndDate" className="text-sm font-medium">
                    End Date <span className="text-destructive">*</span>
                  </Label>
                  <Input
                    id="expensesEndDate"
                    type="date"
                    value={expensesEndDate}
                    onChange={(e) => setExpensesEndDate(e.target.value)}
                    className="h-12"
                    data-testid="input-expenses-end-date"
                  />
                </div>
              </div>

              <div className="bg-muted p-6 rounded-md space-y-2">
                <h3 className="font-semibold text-sm mb-3">Report Includes:</h3>
                <ul className="text-sm text-muted-foreground space-y-1">
                  <li> Total Expenses</li>
                  <li> Category-wise Breakdown</li>
                  <li> Detailed Expense Records</li>
                  <li> Date, Description, and Amount for Each Entry</li>
                </ul>
              </div>

              <Button
                className="w-full h-12"
                onClick={handleExpensesDownload}
                disabled={downloadExpensesMutation.isPending}
                data-testid="button-download-expenses-report"
              >
                <Download className="h-4 w-4 mr-2" />
                {downloadExpensesMutation.isPending ? "Generating Report..." : "Download Excel Report"}
              </Button>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/print-invoice.tsx.content:
import { useRoute, useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { SHOP_INFO } from "@shared/shopInfo";
import logoImg from "@assets/1762677792449-0d76ba93-0eba-48fa-927f-62011c24e28f_1_1762963626825.jpg";
import { useEffect } from "react";

interface InvoiceItem {
  itemName: string;
  hsnCode: string;
  quantity: number;
  rate: string;
  taxableValue: string;
  cgstAmount: string;
  sgstAmount: string;
  total: string;
}

interface InvoiceData {
  id: number;
  invoiceNumber: string;
  invoiceType: string;
  customerName: string;
  customerPhone: string;
  createdAt: string;
  subtotal: string;
  grandTotal: string;
  items: InvoiceItem[];
}

export default function PrintInvoice() {
  const [, params] = useRoute("/print-invoice/:id");
  const [, setLocation] = useLocation();
  const invoiceId = params?.id ? parseInt(params.id) : null;

  const { data: invoice, isLoading } = useQuery<InvoiceData>({
    queryKey: ["/api/invoices", invoiceId],
    enabled: !!invoiceId,
  });

  useEffect(() => {
    if (invoice && !isLoading) {
      setTimeout(() => {
        window.print();
      }, 500);

      const handleAfterPrint = () => {
        if (invoice.invoiceType === "B2B") {
          setLocation("/admin/b2b-invoice");
        } else {
          setLocation("/create-invoice");
        }
      };

      window.addEventListener("afterprint", handleAfterPrint);

      return () => {
        window.removeEventListener("afterprint", handleAfterPrint);
      };
    }
  }, [invoice, isLoading, setLocation]);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <p>Loading invoice...</p>
      </div>
    );
  }

  if (!invoice) {
    return (
      <div className="flex items-center justify-center h-screen">
        <p>Invoice not found</p>
      </div>
    );
  }

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString("en-IN", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
    });
  };

  const formatTime = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString("en-IN", {
      hour: "2-digit",
      minute: "2-digit",
      hour12: true,
    });
  };

  const items = invoice.items.map(item => ({
    itemName: item.itemName,
    hsnCode: item.hsnCode,
    quantity: item.quantity,
    rate: item.rate,
    taxableValue: parseFloat(item.taxableValue),
    cgstAmount: parseFloat(item.cgstAmount),
    sgstAmount: parseFloat(item.sgstAmount),
    total: parseFloat(item.total),
  }));

  const subtotal = parseFloat(invoice.subtotal);
  const grandTotal = parseFloat(invoice.grandTotal);

  return (
    <>
      <style>
        {`
          @media print {
            @page {
              size: 80mm auto;
              margin: 0;
            }
            body {
              margin: 0;
              padding: 0;
            }
          }
        `}
      </style>

      <div className="receipt-container" style={{
        width: "80mm",
        maxWidth: "300px",
        margin: "0 auto",
        padding: "10px",
        fontFamily: "monospace",
        fontSize: "12px",
        lineHeight: "1.4",
      }} data-testid="print-invoice">
        <div style={{ textAlign: "center", marginBottom: "10px" }}>
          <img 
            src={logoImg} 
            alt="Amazeon Logo" 
            style={{
              width: "60px",
              height: "60px",
              margin: "0 auto 8px",
              display: "block",
            }}
          />
          <div style={{ fontWeight: "bold", fontSize: "16px", marginBottom: "2px" }}>
            {SHOP_INFO.name}
          </div>
          <div style={{ fontSize: "10px", marginBottom: "2px" }}>
            {SHOP_INFO.address}
          </div>
          <div style={{ fontSize: "10px", marginBottom: "2px" }}>
            Ph: {SHOP_INFO.phone}
          </div>
          <div style={{ fontSize: "10px", marginBottom: "8px" }}>
            GST: {SHOP_INFO.gstNumber}
          </div>
          <div style={{ borderTop: "1px dashed #000", margin: "8px 0" }} />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", fontSize: "11px" }}>
            <span>Invoice: <strong>{invoice.invoiceNumber}</strong></span>
            <span>{formatDate(invoice.createdAt)}</span>
          </div>
          <div style={{ display: "flex", justifyContent: "space-between", fontSize: "11px" }}>
            <span>Customer: {invoice.customerName}</span>
            <span>{formatTime(invoice.createdAt)}</span>
          </div>
          {invoice.customerPhone && (
            <div style={{ fontSize: "11px" }}>Ph: {invoice.customerPhone}</div>
          )}
        </div>

        <div style={{ borderTop: "1px dashed #000", margin: "8px 0" }} />

        <table style={{ width: "100%", fontSize: "10px", marginBottom: "8px" }}>
          <thead>
            <tr style={{ borderBottom: "1px solid #000" }}>
              <th style={{ textAlign: "left", padding: "2px 0" }}>Item</th>
              <th style={{ textAlign: "center", padding: "2px 0" }}>Qty</th>
              <th style={{ textAlign: "right", padding: "2px 0" }}>Rate</th>
              <th style={{ textAlign: "right", padding: "2px 0" }}>Amt</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <>
                <tr key={index}>
                  <td style={{ padding: "4px 0" }}>{item.itemName}</td>
                  <td style={{ textAlign: "center", padding: "4px 0" }}>{item.quantity}</td>
                  <td style={{ textAlign: "right", padding: "4px 0" }}>{item.rate}</td>
                  <td style={{ textAlign: "right", padding: "4px 0" }}>{item.total.toFixed(2)}</td>
                </tr>
                <tr>
                  <td colSpan={4} style={{ fontSize: "9px", color: "#666", paddingBottom: "4px" }}>
                    HSN: {item.hsnCode} | Tax: {item.taxableValue.toFixed(2)} + {(item.cgstAmount + item.sgstAmount).toFixed(2)} GST
                  </td>
                </tr>
              </>
            ))}
          </tbody>
        </table>

        <div style={{ borderTop: "1px dashed #000", margin: "8px 0" }} />

        <div style={{ fontSize: "11px", marginBottom: "8px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "2px" }}>
            <span>Subtotal:</span>
            <span>{subtotal.toFixed(2)}</span>
          </div>
          <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "4px" }}>
            <span>GST:</span>
            <span>{(grandTotal - subtotal).toFixed(2)}</span>
          </div>
          <div style={{ display: "flex", justifyContent: "space-between", fontWeight: "bold", fontSize: "13px", borderTop: "1px solid #000", paddingTop: "4px" }}>
            <span>TOTAL:</span>
            <span>{grandTotal.toFixed(2)}</span>
          </div>
        </div>

        <div style={{ borderTop: "1px dashed #000", margin: "8px 0" }} />

        <div style={{ textAlign: "center", fontSize: "10px", marginTop: "12px" }}>
          <div>Thank you for your business!</div>
          <div style={{ marginTop: "4px" }}>Visit again</div>
        </div>
      </div>
    </>
  );
}

client/src/pages/settings.tsx.content:
import { useState, useMemo, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle } from "@/components/ui/alert-dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Plus, Key, Trash2 } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { User } from "@shared/schema";

export default function Settings() {
  const { toast } = useToast();

  // User Management State
  const [isAddUserDialogOpen, setIsAddUserDialogOpen] = useState(false);
  const [isResetPasswordDialogOpen, setIsResetPasswordDialogOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [deleteUserId, setDeleteUserId] = useState<string | null>(null);
  const [userFormData, setUserFormData] = useState({
    username: "",
    password: "",
    role: "user" as "admin" | "user",
  });
  const [newPassword, setNewPassword] = useState("");

  // Invoice Series Configuration State
  const [startingInvoiceNumber, setStartingInvoiceNumber] = useState("");

  // GST Calculation Mode State
  const [cashGstMode, setCashGstMode] = useState<"inclusive" | "exclusive">("inclusive");
  const [onlineGstMode, setOnlineGstMode] = useState<"inclusive" | "exclusive">("exclusive");

  // Fetch users
  const { data: users = [], isLoading: usersLoading } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  // Fetch invoice series configuration
  const { data: settings = [] } = useQuery<Array<{ key: string; value: string }>>({
    queryKey: ["/api/settings"],
  });

  // Extract settings from configuration
  useEffect(() => {
    if (settings && settings.length > 0) {
      const invoiceSeriesSetting = settings.find(s => s.key === "invoice_series_start");
      if (invoiceSeriesSetting) {
        setStartingInvoiceNumber(invoiceSeriesSetting.value ?? "");
      }

      const cashGstModeSetting = settings.find(s => s.key === "cash_gst_mode");
      if (cashGstModeSetting) {
        setCashGstMode(cashGstModeSetting.value as "inclusive" | "exclusive");
      }

      const onlineGstModeSetting = settings.find(s => s.key === "online_gst_mode");
      if (onlineGstModeSetting) {
        setOnlineGstMode(onlineGstModeSetting.value as "inclusive" | "exclusive");
      }
    }
  }, [settings]);

  // Calculate Financial Year
  const financialYear = useMemo(() => {
    const today = new Date();
    const currentMonth = today.getMonth(); // 0-11
    const currentYear = today.getFullYear();

    // Financial year starts in April (month 3)
    if (currentMonth >= 3) {
      // April onwards: current year to next year
      const fy1 = currentYear.toString().slice(-2);
      const fy2 = (currentYear + 1).toString().slice(-2);
      return `FY${fy1}-${fy2}`;
    } else {
      // Jan-March: previous year to current year
      const fy1 = (currentYear - 1).toString().slice(-2);
      const fy2 = currentYear.toString().slice(-2);
      return `FY${fy1}-${fy2}`;
    }
  }, []);

  // Preview next invoice number
  const previewInvoiceNumber = useMemo(() => {
    if (!startingInvoiceNumber || isNaN(Number(startingInvoiceNumber))) {
      return `${financialYear}/XXX`;
    }
    const paddedNumber = String(startingInvoiceNumber).padStart(3, '0');
    return `${financialYear}/${paddedNumber}`;
  }, [financialYear, startingInvoiceNumber]);

  // Add User Mutation
  const addUserMutation = useMutation({
    mutationFn: async (data: { username: string; password: string; role: string }) => {
      return await apiRequest("POST", "/api/users", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User added successfully",
      });
      setIsAddUserDialogOpen(false);
      setUserFormData({ username: "", password: "", role: "user" });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to add user",
      });
    },
  });

  // Reset Password Mutation
  const resetPasswordMutation = useMutation({
    mutationFn: async ({ id, password }: { id: string; password: string }) => {
      return await apiRequest("PATCH", `/api/users/${id}/password`, { password });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Password reset successfully",
      });
      setIsResetPasswordDialogOpen(false);
      setSelectedUser(null);
      setNewPassword("");
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to reset password",
      });
    },
  });

  // Delete User Mutation
  const deleteUserMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/users/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User deleted successfully",
      });
      setDeleteUserId(null);
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to delete user",
      });
    },
  });

  // Save Invoice Series Configuration Mutation
  const saveInvoiceSeriesMutation = useMutation({
    mutationFn: async (data: { key: string; value: string }) => {
      return await apiRequest("POST", "/api/settings", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
      toast({
        title: "Success",
        description: "Invoice series configuration saved successfully",
      });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to save configuration",
      });
    },
  });

  // Save GST Mode Configuration Mutation
  const saveGstModesMutation = useMutation({
    mutationFn: async (data: Array<{ key: string; value: string }>) => {
      const promises = data.map(setting => 
        apiRequest("POST", "/api/settings", setting)
      );
      return await Promise.all(promises);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
      toast({
        title: "Success",
        description: "GST calculation modes saved successfully",
      });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Error",
        description: "Failed to save GST modes",
      });
    },
  });

  const handleAddUser = () => {
    if (!userFormData.username || !userFormData.password) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please fill in all required fields",
      });
      return;
    }
    addUserMutation.mutate(userFormData);
  };

  const handleResetPassword = () => {
    if (!selectedUser || !newPassword) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please enter a new password",
      });
      return;
    }
    resetPasswordMutation.mutate({ id: selectedUser.id, password: newPassword });
  };

  const handleDeleteUser = () => {
    if (deleteUserId) {
      deleteUserMutation.mutate(deleteUserId);
    }
  };

  const handleSaveInvoiceSeries = () => {
    if (!startingInvoiceNumber || isNaN(Number(startingInvoiceNumber))) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please enter a valid starting invoice number",
      });
      return;
    }
    saveInvoiceSeriesMutation.mutate({
      key: "invoice_series_start",
      value: startingInvoiceNumber,
    });
  };

  const handleSaveGstModes = () => {
    saveGstModesMutation.mutate([
      { key: "cash_gst_mode", value: cashGstMode },
      { key: "online_gst_mode", value: onlineGstMode },
    ]);
  };

  return (
    <div className="p-8 max-w-7xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-semibold mb-2">Settings</h1>
        <p className="text-muted-foreground">Manage users and system configuration</p>
      </div>

      {/* User Management Section */}
      <Card className="mb-8">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg font-medium">User Management</CardTitle>
            <Dialog open={isAddUserDialogOpen} onOpenChange={setIsAddUserDialogOpen}>
              <DialogTrigger asChild>
                <Button data-testid="button-add-user">
                  <Plus className="h-4 w-4 mr-2" />
                  Add User
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Add New User</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="username">Username <span className="text-destructive">*</span></Label>
                    <Input
                      id="username"
                      value={userFormData.username}
                      onChange={(e) => setUserFormData({ ...userFormData, username: e.target.value })}
                      placeholder="Enter username"
                      data-testid="input-username"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="password">Password <span className="text-destructive">*</span></Label>
                    <Input
                      id="password"
                      type="password"
                      value={userFormData.password}
                      onChange={(e) => setUserFormData({ ...userFormData, password: e.target.value })}
                      placeholder="Enter password"
                      data-testid="input-password"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="role">Role <span className="text-destructive">*</span></Label>
                    <Select
                      value={userFormData.role}
                      onValueChange={(value: "admin" | "user") => setUserFormData({ ...userFormData, role: value })}
                    >
                      <SelectTrigger id="role" data-testid="select-role">
                        <SelectValue placeholder="Select role" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="user" data-testid="option-role-user">User</SelectItem>
                        <SelectItem value="admin" data-testid="option-role-admin">Admin</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <Button 
                    className="w-full" 
                    onClick={handleAddUser} 
                    disabled={addUserMutation.isPending}
                    data-testid="button-submit-user"
                  >
                    {addUserMutation.isPending ? "Adding..." : "Add User"}
                  </Button>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>
        <CardContent>
          {usersLoading ? (
            <div className="text-center py-12 text-muted-foreground">Loading users...</div>
          ) : users.length === 0 ? (
            <div className="text-center py-12 text-muted-foreground">
              No users found.
            </div>
          ) : (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead className="font-semibold">ID</TableHead>
                    <TableHead className="font-semibold">Username</TableHead>
                    <TableHead className="font-semibold">Role</TableHead>
                    <TableHead className="font-semibold text-right">Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {users.map((user) => (
                    <TableRow key={user.id} className="hover-elevate" data-testid={`row-user-${user.id}`}>
                      <TableCell className="font-mono text-sm text-muted-foreground">
                        {user.id.slice(0, 8)}...
                      </TableCell>
                      <TableCell className="font-medium" data-testid={`text-username-${user.id}`}>
                        {user.username}
                      </TableCell>
                      <TableCell>
                        <Badge variant={user.role === "admin" ? "default" : "secondary"} data-testid={`badge-role-${user.id}`}>
                          {user.role}
                        </Badge>
                      </TableCell>
                      <TableCell className="text-right">
                        <div className="flex items-center justify-end gap-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => {
                              setSelectedUser(user);
                              setIsResetPasswordDialogOpen(true);
                            }}
                            data-testid={`button-reset-password-${user.id}`}
                          >
                            <Key className="h-4 w-4 mr-2" />
                            Reset Password
                          </Button>
                          <Button
                            variant="ghost"
                            size="icon"
                            onClick={() => setDeleteUserId(user.id)}
                            data-testid={`button-delete-user-${user.id}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Invoice Series Configuration Section */}
      <Card className="mb-8">
        <CardHeader>
          <CardTitle className="text-lg font-medium">Invoice Series Configuration</CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div>
            <Label className="text-sm font-medium text-muted-foreground">Current Financial Year</Label>
            <p className="text-2xl font-semibold mt-1" data-testid="text-financial-year">{financialYear}</p>
          </div>

          <div className="space-y-2">
            <Label htmlFor="startingNumber">Starting Invoice Number <span className="text-destructive">*</span></Label>
            <Input
              id="startingNumber"
              type="number"
              min="1"
              value={startingInvoiceNumber}
              onChange={(e) => setStartingInvoiceNumber(e.target.value)}
              placeholder="Enter starting number (e.g., 1)"
              data-testid="input-starting-invoice-number"
            />
            <p className="text-xs text-muted-foreground">
              This will set the starting number for new invoices in the current financial year
            </p>
          </div>

          <div className="bg-muted/50 p-4 rounded-lg">
            <Label className="text-sm font-medium text-muted-foreground">Preview</Label>
            <p className="text-lg font-semibold mt-1" data-testid="text-invoice-preview">
              Next invoice will be: {previewInvoiceNumber}
            </p>
          </div>

          <Button 
            onClick={handleSaveInvoiceSeries} 
            disabled={saveInvoiceSeriesMutation.isPending}
            data-testid="button-save-configuration"
          >
            {saveInvoiceSeriesMutation.isPending ? "Saving..." : "Save Configuration"}
          </Button>
        </CardContent>
      </Card>

      {/* GST Calculation Settings Section */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg font-medium">GST Calculation Settings</CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="space-y-2">
            <Label htmlFor="cashGstMode">Cash Payment GST Mode</Label>
            <Select
              value={cashGstMode}
              onValueChange={(value: "inclusive" | "exclusive") => setCashGstMode(value)}
            >
              <SelectTrigger id="cashGstMode" data-testid="select-cash-gst-mode">
                <SelectValue placeholder="Select mode" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="inclusive" data-testid="option-cash-inclusive">
                  Inclusive
                </SelectItem>
                <SelectItem value="exclusive" data-testid="option-cash-exclusive">
                  Exclusive
                </SelectItem>
              </SelectContent>
            </Select>
            <p className="text-xs text-muted-foreground">
              <span className="font-medium">Inclusive:</span> GST is already included in the rate (taxable value = rate  (1 + GST%))
            </p>
            <p className="text-xs text-muted-foreground">
              <span className="font-medium">Exclusive:</span> GST is added on top of the rate (taxable value = rate, GST added separately)
            </p>
          </div>

          <div className="space-y-2">
            <Label htmlFor="onlineGstMode">Online Payment GST Mode</Label>
            <Select
              value={onlineGstMode}
              onValueChange={(value: "inclusive" | "exclusive") => setOnlineGstMode(value)}
            >
              <SelectTrigger id="onlineGstMode" data-testid="select-online-gst-mode">
                <SelectValue placeholder="Select mode" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="inclusive" data-testid="option-online-inclusive">
                  Inclusive
                </SelectItem>
                <SelectItem value="exclusive" data-testid="option-online-exclusive">
                  Exclusive
                </SelectItem>
              </SelectContent>
            </Select>
            <p className="text-xs text-muted-foreground">
              <span className="font-medium">Inclusive:</span> GST is already included in the rate (taxable value = rate  (1 + GST%))
            </p>
            <p className="text-xs text-muted-foreground">
              <span className="font-medium">Exclusive:</span> GST is added on top of the rate (taxable value = rate, GST added separately)
            </p>
          </div>

          <Button 
            onClick={handleSaveGstModes} 
            disabled={saveGstModesMutation.isPending}
            data-testid="button-save-gst-modes"
          >
            {saveGstModesMutation.isPending ? "Saving..." : "Save GST Modes"}
          </Button>
        </CardContent>
      </Card>

      {/* Reset Password Dialog */}
      <Dialog open={isResetPasswordDialogOpen} onOpenChange={setIsResetPasswordDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reset Password</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <p className="text-sm text-muted-foreground">
              Reset password for user: <span className="font-semibold">{selectedUser?.username}</span>
            </p>
            <div className="space-y-2">
              <Label htmlFor="newPassword">New Password <span className="text-destructive">*</span></Label>
              <Input
                id="newPassword"
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                placeholder="Enter new password"
                data-testid="input-new-password"
              />
            </div>
            <Button 
              className="w-full" 
              onClick={handleResetPassword} 
              disabled={resetPasswordMutation.isPending}
              data-testid="button-confirm-reset-password"
            >
              {resetPasswordMutation.isPending ? "Resetting..." : "Reset Password"}
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* Delete User Confirmation Dialog */}
      <AlertDialog open={!!deleteUserId} onOpenChange={() => setDeleteUserId(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Are you sure?</AlertDialogTitle>
            <AlertDialogDescription>
              This action cannot be undone. This will permanently delete the user account.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-delete">Cancel</AlertDialogCancel>
            <AlertDialogAction 
              onClick={handleDeleteUser}
              data-testid="button-confirm-delete"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

